mapper.select:
select count(b.id) from board b join user u on b.user_id = u.id join dept d on u.dept_id = d.id
left outer join file f on b.file_id = f.id
left outer join board_hashtag bh on b.id = bh.board_id
left outer join hashtag h on bh.hashtag_id = h.id
and b.title like "%${text}%" and b.content like "%${text}%" and u.name = #{text} and d.name like "%${text}%" and u.position_id = #{position} and f.name like "%${fileName}%" and f.num like "${fileNum}%" and b.date >= (DATE_SUB(NOW(), INTERVAL #{dateRange} DAY)) and b.date between #{start} and #{end}
h.name = #{hashtagName}

select b.id as board_id, b.title as board_title, d.name as dept_name, b.date as board_date, u.name as user_name, b.file_id as board_fileId from board b join user u on b.user_id = u.id join dept d on u.dept_id = d.id
left outer join file f on b.file_id = f.id
left outer join board_hashtag bh on b.id = bh.board_id
left outer join hashtag h on bh.hashtag_id = h.id
and b.title like "%${text}%" and b.content like "%${text}%" and u.name = #{text} and d.name like "%${text}%" and u.position_id = #{position} and f.name like "%${fileName}%" and f.num like "${fileNum}%" and b.date >= (DATE_SUB(NOW(), INTERVAL #{dateRange} DAY)) and b.date between #{start} and #{end}
h.name = #{hashtagName}
